INSERT INTO `kb` (`id`, `area_id`, `issue`, `solution`, `explanation`, `FROGS`, `FROGSE`, `SROGS`, `RFAS`, `RPDS`) VALUES

	(1, 0, 'Ambiguous', 'Objection, ambiguous.', '<b>Notes</b>\r\nThis is only a valid objection if interrogatory is wholly unintelligible. An interrogatory must be answered if &quot;the nature of the information sought is apparent.&quot; <i>Deyo v Kilbourne</i> (1978) 84 CA3d 771, 783.', 1, 1, 1, 0, 0),
	(2, 0, 'Ambiguous', 'Objection, ambiguous.', '<b>Notes</b>\r\nAmbiguity is only a sufficient ground for refusal to answer if the request is so ambiguous that the responding party cannot in good faith frame an intelligent reply. <i>Cembrook v. Superior Court In and For City and County of San Francisco</i> (1961) 56 Cal.2d 423, 428.', 0, 0, 0, 1, 0),
	(3, 0, 'Ambiguous', 'Objection, ambiguous.', '<b>Notes</b>\r\nThis is only a valid objection if ambiguity precludes an intelligent reply. <i>Cembrook v Superior Court</i> (1961) 56 C2d 423, 430.', 0, 0, 0, 0, 1),
	(4, 0, 'Attorney Work Product', 'This is protected Attorney Work Product.', '', 0, 0, 0, 0, 1),
	(5, 0, 'Attorney-client privilege/Work product', 'Objection, this Interrogatory improperly seeks information protected by the attorney-client privilege and/or attorney work product doctrine.', 'Cal. Judges Benchbook Civ. Proc. Discovery (August 2019) &sect; 18.35, citing Code Civ.Proc., &sect; 2030.240, subd. (b).', 1, 1, 1, 0, 0),
	(6, 0, 'Attorney-client privilege/Work product ', 'Objection, this Request improperly seeks information protected by the attorney-client privilege and/or attorney work product doctrine.', 'Cal. Judges Benchbook Civ. Proc. Discovery (August 2019) &sect; 22.30, citing Code Civ.Proc., &sect; 2033.230, subd. (b).', 0, 0, 0, 1, 0),
	(7, 0, 'Attorney-client privilege/Work product', 'Objection, this Request improperly seeks information protected by the attorney-client privilege and/or attorney work product doctrine.', 'Cal. Judges Benchbook Civ. Proc. Discovery (August 2019) &sect; 19.30, citing Code Civ.Proc., &sect; 2031.240(b)(2), subd. (b)(2).', 0, 0, 0, 0, 1),
	(8, 0, 'Blanket objection to entire set', 'Objection, responding party objects to this entire set of Interrogatories.', 'Cal. Judges Benchbook Civ. Proc. Discovery (August 2019) &sect; 18.35.\r\n\r\n<b>Notes</b>\r\nValid only if every interrogatory is objectionable. Judge should grant motion to compel as to all unobjectionable interrogatories. <i>West Pico Furniture Co. of Los Angeles v. Superior Court In and For Los Angeles County</i> (1961) 56 Cal.2d 407, 418.', 1, 1, 1, 0, 0),
	(9, 0, 'Blanket objection to entire set', 'Objection, responding party objects to this entire set of Requests.', 'Cal. Judges Benchbook Civ. Proc. Discovery (August 2019) &sect; 22.30.\r\n\r\n<b>Notes</b>\r\nValid only if each request is objectionable. Judge should grant motion to compel as to all unobjectionable requests. <i>West Pico Furniture Co. of Los Angeles v. Superior Court In and For Los Angeles County</i> (1961) 56 Cal.2d 407, 418.', 0, 0, 0, 1, 0),
	(10, 0, 'Blanket objection to entire set', 'Objection, responding party objects to this entire set of Requests.', 'Cal. Judges Benchbook Civ. Proc. Discovery (August 2019) &sect; 19.30.\r\n\r\n<b>Notes</b>\r\nValid only if each request is objectionable. Judge should grant motion to compel as to all unobjectionable requests. <i>West Pico Furniture Co. of Los Angeles v. Superior Court In and For Los Angeles County</i> (1961) 56 Cal.2d 407, 418 (as to interrogatories).', 0, 0, 0, 0, 1),
	(11, 0, 'Combined discovery methods', 'Objection, this Request improperly combines different discovery methods.', 'Cal. Judges Benchbook Civ. Proc. Discovery (August 2019) &sect; 22.30, citing Code Civ.Proc., &sect; 2033.060, subd. (h).', 0, 0, 0, 1, 0),
	(12, 0, 'Compound/conjunctive/disjunctive', 'Objection, this Interrogatory is compound, conjunctive, or disjunctive.', 'Code Civ.Proc., &sect; 2030.060, subd. (f).', 0, 0, 1, 0, 0),
