	(14, 0, 'Confidential', 'Objection, confidential.', 'Cal. Judges Benchbook Civ. Proc. Discovery (August 2019) &sect; 18.35, citing Code Civ.Proc., &sect; 2030.240, subd. (b).\r\n\r\n<b>Notes</b>\r\nThis is only a valid objection if an applicable Confidentiality privilege is claimed.', 1, 1, 1, 0, 0),
	(15, 0, 'Confidential', 'Objection, confidential.', 'Cal. Judges Benchbook Civ. Proc. Discovery (August 2019) &sect; 22.30, citing Code Civ.Proc., &sect; 2033.230, subd. (b).\r\n\r\n<b>Note</b>\r\nThis is only a valid objection if an applicable Confidentiality privilege is claimed.', 0, 0, 0, 1, 0),
	(16, 0, 'Confidential', 'Objection, confidential.', 'Cal. Judges Benchbook Civ. Proc. Discovery (August 2019) &sect; 19.30, citing Code Civ.Proc., &sect; 2031.240, subd. (b).\r\n\r\n<b>Notes</b>\r\nThis is only a valid objection if an applicable Confidentiality privilege is claimed.', 0, 0, 0, 0, 1),
	(17, 0, 'Continuing Interrogatory', 'Objection, continuing interrogatory.', 'Cal. Judges Benchbook Civ. Proc. Discovery (August 2019) &sect; 18.35, citing Code Civ.Proc., &sect; 2030.060, subd. (g).\r\n\r\n<b>Notes</b>\r\nAn interrogatory may not be made a continuing one so as to impose on the party responding to it a duty to supplement an answer to it that was initially correct and complete with later acquired information.', 1, 1, 1, 0, 0),
	(18, 0, 'Cumulative', 'Objection, this Interrogatory is cumulative.', 'Cal. Judges Benchbook Civ. Proc. Discovery (August 2019) &sect; 18.35, citing Code Civ.Proc., &sect; 2019.030, subd. (a)(1).\r\n\r\n<b>Notes</b>\r\nValid only if discovery is unreasonably cumulative or duplicative.', 1, 1, 1, 0, 0),
	(19, 0, 'Documents not attached', 'Objection, documents not attached.', 'Cal. Judges Benchbook Civ. Proc. Discovery (August 2019) &sect; 22.30, citing Code Civ.Proc., &sect; 2033.060, subd. (g).\r\n\r\n<b>Notes</b>\r\nOnly a valid objection for a Request for Admission of the genuineness of a document.', 0, 0, 0, 1, 0),
	(20, 0, 'Equally available to propounding party', 'Objection, the information sought is equally available to propounding party.', 'Cal. Judges Benchbook Civ. Proc. Discovery (August 2019) &sect; 18.35, citing Code Civ.Proc., &sect; 2030.220, subd. (c) and <i>Alpine Mut. Water Co. v Superior Court</i> (1968) 259 CA2d 45, 54.\r\n\r\n<b>Notes</b>\r\nJudicial Council forms, i.e. Form Interrogatories, are presumptively valid. Gov. Code, &sect; 68511; Code Civ.Proc., &sect; 2033.710.', 1, 1, 0, 0, 0),
	(21, 0, 'Equally available to propounding party', 'Objection, the information sought is equally available to propounding party.', 'Cal. Judges Benchbook Civ. Proc. Discovery (August 2019) &sect; 18.35, citing Code Civ.Proc., &sect; 2030.220, subd. (c) and <i>Alpine Mut. Water Co. v Superior Court</i> (1968) 259 CA2d 45, 54.\r\n\r\n<b>Notes</b>\r\n<i>Alpine Mut. Water Co. v Superior Court</i> (1968) 259 CA2d 45 is limited to publicly available documents.', 0, 0, 1, 0, 0),
	(22, 0, 'Equally available to propounding party', 'Objection, the information sought is equally available to propounding party.', 'Cal. Judges Benchbook Civ. Proc. Discovery (August 2019) &sect; 19.30, citing Code Civ.Proc., &sect; 2031.230.\r\n\r\n<b>Notes</b>\r\nValid only if the demand would require respondent to make more than a &quot;reasonable inquiry.&quot;', 0, 0, 0, 0, 1),
